import "./App.css";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import Home from "./pages/Home";
import CreatePost from "./pages/CreatePost";
import Login from "./pages/Login";
import { useState } from "react";
import { signOut } from "firebase/auth";
import { auth } from "./firebase-config";

function App() {
  const [isAuth, setIsAuth] = useState(localStorage.getItem("isAuth"));

  const signUserOut = () => {
    signOut(auth).then(() => {
      localStorage.clear();
      setIsAuth(false);
      window.location.pathname = "/login";
    });
  };

  return (

    <Router>
      <nav>
      <div>
        <img src="https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.woshipm.com%2Foperate%2F4378726.html&psig=AOvVaw0OPZF4DXR6X6xhCXBralJr&ust=1667804527487000&source=images&cd=vfe&ved=0CAsQjRxqFwoTCPj4jLb-mPsCFQAAAAAdAAAAABAE"/>
      </div>
        <Link className="wlogin" to="/"> Home </Link>

        {!isAuth ? (
          <Link className="wlogin" to="/login"> Login </Link>
        ) : (
          <>
            <Link className="wlogin" to="/createpost"> Create Post </Link>
            <button className="logout" onClick={signUserOut}> Log Out</button>
          </>
        )}
      </nav>
      <Routes>
        <Route path="/" element={<Home isAuth={isAuth} />} />
        <Route path="/createpost" element={<CreatePost isAuth={isAuth} />} />
        <Route path="/login" element={<Login setIsAuth={setIsAuth} />} />
      </Routes>
    </Router>
  );
}

export default App;
